window.MicroChart={bar:function(c,l,d){const x=c.getContext('2d');const W=c.width,H=c.height;x.clearRect(0,0,W,H);const m=Math.max(1,...d),p=30,s=(W-p*2)/d.length,b=s*0.7;const pc=getComputedStyle(document.documentElement).getPropertyValue('--primary').trim();x.font='12px sans-serif';d.forEach((v,i)=>{const h=(H-p*2)*(v/m),X=p+i*s+(s-b)/2,Y=H-p-h;x.fillStyle=pc||'#8ab4f8';x.fillRect(X,Y,b,h);x.fillStyle=getComputedStyle(document.documentElement).getPropertyValue('--muted').trim();x.fillText(l[i]||'',X,H-p/2);});}};